    margin: 15px;
}

.search__title {
    text-align: center;
}

.search__form {
    margin-top: 5px;
    text-align: center;
}

.search__input {
    border: none;
    border-radius: 25px;
    background-image: url('/e4bff3e2fbca9d73af595192f23e4b89.svg');
    background-position: 10px 7px;
    background-repeat: no-repeat;
    background-size: 35px;
    padding: 12px 0px 12px 61px;
}

.search__btn {
    margin-left: 5px;
    padding: 13px;
    background-color: #fff;
    border-radius: 25px;
    border: none;
    transition: all 0.1s linear;
}

.search__btn:hover {
    box-shadow: 0 0 50px -5px rgba(0, 0, 0, 0.25);
    background-color: #222831;
    color: #fff;
    cursor: pointer;
}

.cant-find {
    display: none;
    margin-top: 100px;
}

