    padding: 25px;
    border-radius: 25px;
    background-image: linear-gradient(120deg, #66a6ff 0%, #89f7fe 100%);
    height: 400px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    min-width: 300px;
    z-index: 10;
    transition: all 0.2s linear;
}

.result-block__temp:hover {
    transform: scale(1.05) perspective(1200px) rotateY(10deg);
    ;
