.temp__temperature {
    font-size: 4em
}

.location__city {
    margin-top: 10px;
}

.result-block__days {
    margin: 10px;
    margin-left: -10px;
    padding: 40px;
    border-top-right-radius: 25px;
    border-bottom-right-radius: 25px;
    background-color: #222831;
}

.res-block {
    display: flex;
    justify-content: space-between;
}

.res-block+.res-block {
    margin-top: 10px;
}

.res-block__title {
    font-weight: 700;
}

.week-list {
    margin-top: 25px;
    margin-left: 0;
    padding-left: 0;
    display: flex;
    flex-direction: row-reverse;
    justify-content: center;
    box-shadow: 0 0 50px -5px rgba(0, 0, 0, 0.25);
    border-radius: 10px;
}

.day-block {
    list-style-type: none;
    display: flex;
    flex-direction: column;
    padding: 15px;
    padding-top: 50px;
    background-color: #222831;
    border-radius: 10px;
    transition: all 0.1s linear;
}

.day-block:hover {
    background: #fff;
    color: #222831;
    cursor: pointer;
    transform: scale(1.05);
}

.day-block_active {
    background: #fff;
    color: #222831;
}

.day__temp {
    text-align: center;
    font-weight: 700;
}

.day__name {
    text-align: center;
}

